from flask import Flask, redirect, url_for
import os 
# getting app

app = Flask(__name__)

@app.route('/')
def home():
    return '''
    <html>
    <head>
    <style>
    body {background-color: powderblue;}
    h1   {color: blue;}
    p    {color: red;}
    </style>
    </head>
    <body>

<h1>!!! Info on flask !!!</h1>
<p>This website is created using flask.</p>
<img src="data:image/png;base64,">
<a href="https://flask.palletsprojects.com/en/2.0.x/">Flask Documentation</a>

    </body>
    </html>
    '''

@app.route('/<name>')
def user(name):
    return f"Hello {name}"

@app.route('/admin')
def admin():
    return redirect(url_for("home"))

if __name__ == "__main__":
    app.run()